const { validationError } = require("express-validator");

class CustomError extends Error {
  constructor(message) {
    super(message);
    if (this.constructor == CustomError) {
      throw new Error("Abstract classes can't be instantiated.");
    }
    // assign the error class name in your custom error (as a shortcut)
    this.name = this.constructor.name;

    // capturing the stack trace keeps the reference to your error class
    Error.captureStackTrace(this, this.constructor);

    // you may also assign additional properties to your error
    this.isSleepy = true;
  }
  getStatusCode() {
    throw new Error("Method 'getStatusCode()' must be implemented.");
  }
  serializeErrors() {
    throw new Error("Method 'serializeErrors()' must be implemented.");
  }
}
module.exports = CustomError;
