const jwt = require("jsonwebtoken");
const config = require("../config/config");

module.exports = (req, res, next) => {
  //requested token type "Bearer gfgsgshahhahshs"
  //Bearer keyword is necessary in the begining,that is the reason to split with white spaces
  //and to take the rest as token
  try {
    //const token = req.headers.authorization.split(" ")[1];
    const token = req.session.jwt;
    const decodedToken = jwt.verify(token, config.secret);
    req.userAutorizationData = {
      userId: decodedToken.id,
      userEmail: decodedToken.email,
      userMobile: decodedToken.mobile,
      userUsername: decodedToken.username,
    };
    next();
  } catch (error) {
    return res.status(401).json({
      status: "failed",
      comment: "Authentication Token Expired",
      token: null,
      remainingCount: null,
      expiration: null,
      userData: null,
    });
  }
};
