const RequestValidationError = require("../errors/request-validation-error");
const DatabaseConnectionError = require("../errors/database-connection");
const CustomError = require("../errors/custom-error");
const errorHandler = (err, req, res, next) => {
  if (err instanceof CustomError) {
    return res.status(err.getStatusCode()).json(err.serializeErrors());
  }
  res.status(401).json({
    status: "failed",
    comment: err.message,
    errors: {
      message: err.message,
      filed: "something went wrong!",
    },
  });
};
module.exports = errorHandler;
